 defined( '_JEXEC' ) or die(); class plgContentAimyChartsInstallerScript { const V_JOOMLA_MIN = '3.0'; const V_PHP_MIN = '5.3.10'; public function preflight( $type, $parent ) { return ( self::check_php_version( self::V_PHP_MIN ) && self::check_joomla_version( self::V_JOOMLA_MIN ) ); } public function postflight( $route, $adapter ) { $task = strtolower( $route ); if ( $task != 'install' && $task != 'update' ) { return; } if ( $task == 'update' ) { require( __DIR__ . DIRECTORY_SEPARATOR . 'UpdateServer.php' ); AimyChartsUpdateServer::cleanup_server_list( $adapter ); $path = JPATH_ROOT . '/media/plg_aimycharts/Chart.min.js'; if ( JFile::exists( $path ) ) { try { JFile::delete( $path ); } catch ( Exception $e ) { } } } JFactory::getLanguage()->load( 'plg_content_aimycharts', JPATH_ADMINISTRATOR ); echo '<div style="padding:32px;text-align:center;">', '<h1>', '<img src="', JURI::base(), '../media/plg_aimycharts/aimy-logo_340x327.png" ', 'width="340" height="327" alt="Aimy" />', '<br/>', 'Aimy Charts  v3.11.0', '</h1>', '<p class="lead">', JText::_( $task == 'update' ? 'AIMY_CHARTS_HINT_UPDATED' : 'AIMY_CHARTS_HINT_INSTALLED' ), '!', '</p>'; $cfg_url = false; $enabled = false; try { $db = JFactory::getDbo(); $q = $db->getQuery( true ); $q->select( $db->quoteName( array( 'extension_id', 'enabled' ) ) ) ->from( $db->quoteName( '#__extensions' ) ) ->where( $db->quoteName( 'element' ) . ' = ' . $db->quote( 'aimycharts' ) ) ->where( $db->quoteName( 'type' ) . ' = ' . $db->quote( 'plugin' ) ); $db->setQuery( $q ); $row = $db->loadObject(); if ( $row && $row->extension_id ) { $cfg_url = JRoute::_( 'index.php?' . 'option=com_plugins&task=plugin.edit&' . 'extension_id=' . $row->extension_id ); $enabled = $row->enabled; } } catch ( Exception $e ) {} echo '<p>'; if ( $cfg_url ) { echo self::_btn( $cfg_url, JText::_( ! $enabled ? 'AIMY_CHARTS_HINT_ENABLE_CONFIGURE' : 'AIMY_CHARTS_HINT_CONFIGURE' ) ), ' &nbsp; '; } echo self::_btn( 'https://www.aimy-extensions.com/joomla/charts.html#user-manual', JText::_( 'AIMY_CHARTS_HINT_READ_MANUAL' ), true ), ' &nbsp; ', self::_btn( 'http://static.aimy-extensions.com/images/products/charts/plg-aimy-charts.pdf?v=3.11.0', JText::_( 'AIMY_CHARTS_HINT_DL_MANUAL' ), false ), '</p>'; echo '<p style="padding:12px 0;" />', '<a href="https://www.aimy-extensions.com/joomla/charts.html" target="_blank">https://www.aimy-extensions.com/joomla/charts.html</a>', '</p>', '</div>', "\n"; } static private function _btn( $url, $text, $new_tab = false ) { return '<a class="btn btn-lg btn-success" href="' . $url . '" ' . ( $new_tab ? 'target="_blank" ' : '' ) . 'role="button">' . $text . '!' . '</a>'; } static private function check_php_version( $min ) { if ( version_compare( PHP_VERSION, $min, '<' ) ) { JFactory::getApplication()->enqueueMessage( 'You are currently using PHP ' . PHP_VERSION . ', ' . 'but Aimy Charts requires at least PHP ' . $min . '.', 'error' ); return false; } return true; } static private function check_joomla_version( $min ) { $jv = defined( 'JVERSION' ) ? JVERSION : 0; if ( version_compare( $jv, $min, '<' ) ) { JFactory::getApplication()->enqueueMessage( 'You are currently using Joomla! ' . $jv . ', ' . 'but Aimy Charts requires at least Joomla! ' . $min . '.', 'error' ); return false; } return true; } } 
